INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001441, N'أنظمة صوت كاريوكي منزلية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز صوت منزلي يقوم بتشغيل مسار الدعم لأغنية مما يسمح للشخص بغناء غناء الرصاص ، من خلال ميكروفون متصل. تحتوي بعض أنظمة الكاريوكي على شاشة مدمجة تعرض كلمات الأغنية في الوقت المناسب مع اللحن.', N'يستثنى من ذلك منتجات مثل طوابق كاسيت الصوت المنزلي أو الأقراص المضغوطة أو MD وأنظمة الاستريو المنزلي.')
