INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001434, N'طوابق MD الصوت المنزلي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز صوت منزلي مصمم خصيصًا لتشغيل أقراص الصوت الصغيرة في المنزل. يتضمن ذلك جميع مشغلات MD التي تحتوي على وظيفة تسجيل.', N'باستثناء منتجات مثل كاسيت الصوت المنزلي وأقراص الأقراص المضغوطة ، بالإضافة إلى مشغلات MD الصوتيات الشخصية والسيارات.')
