INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003776, N'النظارات المكبرة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أداة بصرية محمولة ومنحنية أحادية العدسة ، مصممة خصيصًا لتكبير الرؤية ، وتهدف عادةً إلى تزويد المستخدم بصورة محسنة للأشياء القريبة والطباعة.', N'باستثناء المنتجات مثل المناظير ، والمناظير الأحادية ، والتلسكوبات ، والمجاهر.')
