INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001502, N'المجاهر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة بصرية متعددة العدسات مصممة لتكبير الرؤية وتزويد المستخدم بصورة محسنة للغاية لأجسام صغيرة جدًا ، والتي غالبًا ما تكون غير مرئية للعين المجردة. لعرض الأشياء ، يجب تثبيتها على شريحة معدة ، موضوعة تحت العدسات. عادة ما يكون لهذه المنتجات تطبيق علمي.', N'باستثناء المنتجات مثل المناظير والنظارات المكبرة والمناظير الأحادية والتلسكوبات.')
