INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001505, N'بصريات - قطع غيار / ملحقات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جزء بديل أو ملحق للمنتجات ضمن فئة البصريات.', N'باستثناء المنتجات مثل البطاريات وجميع منتجات البصريات التي يتم تقديمها حاليًا في فئة البصريات.')
