INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001487, N'الكاميرات الرقمية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز رقمي مصمم خصيصًا لالتقاط الصور الفوتوغرافية المخزنة إما على ذاكرة داخلية أو بطاقات / شرائح ذاكرة. يمكن عرض الصور الملتقطة بواسطة كاميرا رقمية على منتجات مثل أجهزة التلفزيون أو أجهزة الكمبيوتر. يمكن مسح الصور غير المرغوب فيها من الذاكرة.', N'باستثناء منتجات مثل الكاميرات التناظرية.')
