INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001495, N'ألبومات الصور / مكعبات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها ككتاب أو صندوق أو مكعب على قاعدة ، وهو مصمم خصيصًا لتخزين الصور الفوتوغرافية وعرضها. يمكن وضع الصور خلف فيلم بلاستيكي أو عرضها داخل إطار.', N'باستثناء منتجات مثل إطارات الصور والألبومات القابلة للتحصيل والشرائح.')
