INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000481, N'سلس البول عند البالغين - ضمادات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها وسادة ماصة من المواد ، مصممة خصيصًا لسلس البول لدى البالغين للمساعدة في احتواء الفضلات الجسدية وحماية الملابس. تشمل على وجه التحديد المنتجات المصممة للارتداء داخل الملابس الداخلية أو سراويل سلس البول.', N'يُستثنى من ذلك المنتجات مثل الملابس الداخلية لسلس البول للبالغين ، والبطانات اليومية والمناديل الصحية أو المناشف.')
