INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000321, N'سلس البول الكبار - اللوازم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كعنصر مصمم للاستخدام مع منتجات سلس البول للبالغين لتعزيز أو تكملة سلامة وراحة وراحة أولئك الذين يعانون من سلس البول. تشمل المنتجات أيضًا ملاءات السرير الواقية التي تستخدم لمرة واحدة والتي لا تستخدم لمرة واحدة ومنصات المقاعد.', N'باستثناء منتجات مثل الملابس الداخلية لسلس البول للبالغين ، وحفاضات الأطفال ، وفوط الملابس الداخلية ، والمناديل الصحية أو المناشف.')
