INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000709, N'سلس البول للبالغين - ملابس داخلية (يمكن التخلص منها)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملابس داخلية واقية يمكن التخلص منها ومصممة خصيصًا لسلس البول لدى البالغين ، للمساعدة في احتواء الفضلات الجسدية وحماية الملابس. قد تتطلب أو لا تتطلب إضافة ضمادات سلس البول.', N'يُستثنى من ذلك المنتجات مثل الملابس الداخلية التي لا تستخدم لمرة واحدة ، وسادات سلس البول للبالغين ، والفوط اليومية ، والمناديل الصحية أو المناشف.')
