INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000813, N'غسول الجسم أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجات غسول الجسم ، حيث لا يستطيع مستخدم المخطط تصنيف المنتجات في الطوب الموجود داخل المخطط.', N'باستثناء جميع المنتجات المصنفة حاليًا على أنها منتجات غسول الجسم.')
