INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000334, N'مستلزمات التنظيف / الغسيل - شخصي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كملحق مصمم خصيصًا للاستخدام في الحمام / الدش. يشمل المنتجات التي تهدف عادةً إلى تحسين الراحة والأمان. يشمل على وجه التحديد قبعات الاستحمام ومواد التقشير المعمرة.', N'يستثنى من ذلك منتجات مثل مستحضرات التقشير ، والمناشف القماشية / المنسوجة ، ومنتجات تدليك الحمام والتنغيم.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000330, N'منظف / غسيل / صابون - للجسم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستحضر مصمم لتنظيف وغسيل الجسم ويقصد به عادةً وضعه على الجلد لإزالة الجراثيم والرواسب غير المرغوب فيها من الأوساخ والأوساخ والزيوت. يمكن استخدام هذه المنتجات مع الماء أو بدونه. قد تدعي المنتجات أنها تقشر أو ترطب بالإضافة إلى التطهير ، لكن المنتجات المصممة خصيصًا للتقشير والترطيب مستثناة. تشمل المواد الصلبة والرغوة والصابون السائل وجل الاستحمام. قد تدعي المنتجات أنها تحتوي على تركيبات جلدية مثل مضادات الحساسية والفيتامينات المضافة والمرطبات والمطريات.', N'باستثناء منتجات مثل إضافات الاستحمام والشامبو ومنظفات الوجه / مزيلات المكياج والمقشرات والمرطبات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000573, N'مناديل - شخصية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ورق أو منديل رطب يمكن التخلص منه ، ومصممة خصيصًا لتطهير و / أو تطهير الجلد ، ويتم تسويق بعض هذه المنتجات لاستخدامها في مناطق معينة من الجسم. يشمل منتجات مثل مناديل النظافة النسائية ومناديل الأطفال المبللة.', N'يستثنى من ذلك المنتجات مثل مناديل التواليت ومناديل التجميل أو مناديل الوجه.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000324, N'العناية بعد الحلاقة', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستحضر ، مصممة خصيصًا لتطبيقها على الجلد بعد الحلاقة ، بهدف أساسي هو تهدئة وترطيب وتوحيد لون البشرة وتقليل التهيج وعدم الراحة بدلاً من إعطاء رائحة لطيفة.', N'يستثني على وجه التحديد العطور والعطور ومنتجات العناية بإزالة الشعر ، والغرض الأساسي منها هو استخدامه قبل أو أثناء الحلاقة أو إزالة الشعر. يستثنى من ذلك منتجات مثل منتجات العناية بإزالة الشعر ومرطبات البشرة ومعطرات ومرطبات البشرة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006275, N'مرطبات بعد التعرض للشمس', N'يشمل أي منتجات يمكن تصميمها وتسويقها لاستخدامها بعد جلسات التسمير بالشمس أو أثناءها. مصمم خصيصًا للتطبيق على أي جزء أو مجموعة من أجزاء الجسم ، ويهدف إلى إعادة الرطوبة إلى الجلد وعلاج و / أو منع تجاعيد الوجه أو الأمراض الجلدية الجافة.', N'يستثني على وجه التحديد جميع المرطبات التي لم يتم تصميمها وتسويقها لاستخدامها بعد أو أثناء جلسات التسمير بالشمس. يستثنى من ذلك منتجات مثل بودرة التلك وشامبو الشعر بعد التعرض للشمس.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000327, N'مساعدات مكافحة البقع (بدون طاقة)', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز غير مزود بالطاقة ومصمم خصيصًا لإزالة الرؤوس السوداء / الرؤوس البيضاء مع الحد الأدنى من الصدمات التي تلحق بالجلد. قد تشتمل المنتجات على شرائط وملاعق خشبية ومعدنية ومضخات / محاقن تعمل بالشفط.', N'يستثني على وجه التحديد جميع الأجهزة التي تعمل بالطاقة والمصممة لإزالة الرؤوس السوداء / الرؤوس البيضاء. يستثنى من ذلك المنتجات مثل مضادات البقع التي تعمل بالطاقة ، والمنظفات المضادة للبقع / حب الشباب وعلاجات حب الشباب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007277, N'إكسسوارات مسار الستائر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مصمم خصيصًا للاستخدام مع مسارات الستائر. يشمل جميع تركيبات مسار الستائر.', N'باستثناء ملحقات وأقواس عمود الستائر / القضيب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003906, N'عتبات الباب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كشريط أفقي مصمم خصيصًا ليكون بمثابة عتبة باب لتشكيل قاع المدخل وتقديم الدعم عند المرور عبر المدخل.', N'يستثني أي أجهزة باب أخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000806, N'مساعدات مكافحة البقع (بالطاقة)', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة تعمل بالطاقة تهدف إلى منع وإزالة البقع ، وإصلاح مشاكل المسام المسدودة وإزالة حب الشباب والرؤوس السوداء والشوائب الأخرى مع الحد الأدنى من الصدمة للجلد. يشمل منتجات مثل مزيلات الشوائب القوية وبنادق منظف المسام.', N'يستثني على وجه التحديد جميع الأجهزة التي لا تعمل بالطاقة والمصممة لإزالة الرؤوس السوداء / الرؤوس البيضاء. يستثنى من ذلك منتجات مثل مضادات البقع التي لا تعمل بالطاقة وكريمات حب الشباب التي تعالج وتحد من أمراض الجلد مثل حب الشباب والوردية والصابون / جل الاستحمام التي تستخدم لتنظيف وغسل البشرة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000332, N'منظفات / مزيلات مستحضرات التجميل (بدون طاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستحضر غير مزود بالطاقة يستخدم على الوجه مصمم لتنظيف / إزالة مستحضرات التجميل من الجلد. قد تدعي المنتجات أنها تقشر أو ترطب بالإضافة إلى التطهير ، وتدعي أنها تحتوي على تركيبات جلدية مثل هيبوالرجينيك والفيتامينات المضافة واللانولين. يشمل منتجات مثل شرائط الوجه والجل والكريمات.', N'يستثني على وجه التحديد جميع المنظفات ومزيلات مستحضرات التجميل التي تعمل بالطاقة مثل حمامات البخار للوجه وأي منتجات يكون الغرض الأساسي منها هو التقشير أو الترطيب. باستثناء منتجات مثل المنظفات الكهربائية ومزيلات مستحضرات التجميل ، ومنتجات تنظيف الجسم / الغسيل للاستخدام في الحمام / الدش ، ومضادات البقع ، وعلاجات حب الشباب ، والمقشرات ، والمرطبات ، وحزم الوجه والأقنعة ، والمرحاض ، وصابون الاستحمام والاستحمام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000808, N'المنظفات / مزيلات مستحضرات التجميل (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة تعمل بالطاقة التي تستخدم لتنظيف وإزالة مستحضرات التجميل من الجلد مثل أنظمة البخار المحمولة ، والتي تستخدم لتطهير الجلد عن طريق فتح مسام الجلد. يشمل منتجات مثل حمامات البخار للوجه.', N'يستثنى على وجه التحديد جميع المنظفات ومزيلات مستحضرات التجميل التي لا تعمل بالطاقة مثل شرائط الوجه والجل والكريمات. يستثني المنتجات مثل المنظفات غير بالطاقة / مزيلات مستحضرات التجميل مثل المناديل وعلاجات الجلد مثل كريم حب الشباب المصممة للاستخدام في علاج وتخفيف الأمراض الجلدية مثل حب الشباب والثآليل والوردية والفيروكاس ومنتجات تنظيف البشرة العامة مثل الصابون ، جل الاستحمام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000719, N'تبريد الوجه / الجسم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمنتج لتبريد البشرة مصمم خصيصًا للتطبيق على الوجه و / أو الجسم ، ويهدف إلى تبريد البشرة وإنعاشها. قد تحتوي المنتجات على ادعاءات إضافية مثل ترطيب البشرة وتلطيفها وترطيبها وتوحيد لونها وتخدير حكة الجلد.', N'يستثنى من ذلك منتجات مثل مرطبات الوجه والجسم ومرطبات ما بعد التعرض للشمس.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000342, N'مقشرات / أقنعة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستحضر يوضع على الجلد من أجل تقشير الجلد ، أو يوضع على الجلد ويُسمح له بالبقاء لفترة قصيرة قبل إزالته ، وهو مصمم خصيصًا للتنظيف أو الشد أو الانتعاش أو ترطيب البشرة. قد تدعي المنتجات أن لها تركيبات جلدية مثل التنغيم ومضاد للحساسية. يشمل على وجه التحديد أقنعة ومقشرات / مقشرات للجسم والوجه.', N'يستثنى من ذلك منتجات مثل منظفات الوجه ، ومزيلات مستحضرات التجميل ، وكريمات ترطيب الوجه ، وتقشير البشرة ، وصابون التواليت ، وصابون الاستحمام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005727, N'بلسم الشفاه', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها بلسم مُصمم لشفاء وإصلاح وترطيب الشفاه. بالإضافة إلى ذلك ، يمكن صبغ المنتجات لتحسين مظهر الشفاه. يشمل المنتجات المقدمة على شكل تكوين زيتي أو هلام أو عصا وبعض المنتجات ذات نكهة.', N'يستثنى من ذلك منتجات مثل مستحضرات تجميل الشفاه ومنتجات الحماية من الشمس ومنتجات ترطيب العناية بالبشرة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000810, N'العناية بالبشرة - قطع غيار', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجزاء بديلة لمنتجات العناية بالبشرة.', N'باستثناء المنتجات مثل شرائط التنظيف وجميع منتجات العناية بالبشرة الأخرى التي يتم تقديمها حاليًا ضمن فئة العناية بالبشرة ، بالإضافة إلى المنتجات مثل جميع المنتجات الموجودة في فئة تدليك الجسم والتنغيم.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000812, N'العناية بالبشرة أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجات للعناية بالبشرة ، حيث لا يستطيع مستخدم المخطط تصنيف المنتجات الموجودة في الطوب الموجود داخل المخطط.', N'باستثناء جميع المنتجات المصنفة حاليًا على أنها منتجات تنظيف البشرة ومنتجات تسمير البشرة وتدليك الجسم ومنتجات التنغيم ومستحضرات التجميل والعطور.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000721, N'عبوات متنوعة للعناية بالبشرة', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما اثنان أو أكثر من منتجات العناية بالبشرة المميزة التي تباع معًا ، والتي توجد داخل المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر موجودان في نفس العبوة التي تتقاطع مع الطوب داخل فئة العناية بالبشرة. يشمل منتجات مثل مقشر ومرطبات الوجه التي تباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل مرطبات الجسم وحزم العطور المتنوعة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000356, N'منتجات العناية بالبشرة / الترطيب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمنتج مصمم خصيصًا للتطبيق على أي جزء أو مجموعة من أجزاء الجسم ، ويهدف إلى استعادة الرطوبة للبشرة و / أو علاج و / أو منع تجاعيد الوجه أو أمراض الجلد الجافة . قد تحتوي المنتجات على ادعاءات إضافية مثل الترطيب ، وشد البشرة ، ورفعها ، ومكافحة السيلوليت ، والفيتامينات ، والحماية من أشعة الشمس أو الخصائص المضادة للأكسدة.', N'يستثني على وجه التحديد المرطبات ، التي تم تصميمها وتسويقها لاستخدامها بعد أو أثناء جلسات التسمير بالشمس والمنتجات المصممة والمُسوَّقة لاستخدامها كعناية ببشرة الأمومة. يستثنى من ذلك منتجات مثل بودرة التلك ومزيلات / منظفات مستحضرات التجميل ومستحضرات العناية بعد الحلاقة وعلاجات البشرة الخاصة بحالات جلدية معينة مثل الأكزيما وحب الشباب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000374, N'مسحوق تجفيف الجلد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مسحوق ناعم ، وهو مصمم خصيصًا لترطيب البشرة ويتم استخدامه عادةً بعد الاستحمام لامتصاص الرطوبة ومنع الاحتكاك. قد تدعي المنتجات تركيبات مثل خصائص مطهرة أو طبية.', N'باستثناء منتجات مثل مسحوق إزالة الروائح الكريهة ومنتجات الترطيب / التنغيم الخاصة والعامة والبودرة المصممة خصيصًا لعلاج الحالات الطبية مثل قدم الرياضيين.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000484, N'أحبار / قابض', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستحضر ، يتم دهنها أو رشها على الوجه ، وهي مصممة خصيصًا لاستعادة تماسك البشرة عن طريق شد المسام وتنظيف البشرة. قد تدعي المنتجات أن لها تركيبات جلدية مثل مضادات البكتيريا وخالية من الكحول والترطيب والحماية من أشعة الشمس.', N'يستثنى من ذلك المنتجات مثل وسادات معطر البشرة ومرطبات الوجه ومبردات / منظفات الوجه ومنظفات الوجه ومزيلات المكياج.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000717, N'عبوات متنوعة لمنتجات البشرة', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما منتجان أو أكثر من منتجات البشرة المميزة التي يتم بيعها معًا والموجودة في المخطط ولكنها تنتمي إلى فئات مختلفة ، أي منتجين أو أكثر متضمنين في نفس العبوة والتي تتقاطع مع فئات ضمن مجموعة منتجات البشرة. يشمل منتجات مثل كريمات الشمس مع عبوات متنوعة بعد التعرض للشمس. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل قفازات Body Buffer وجل الاستحمام المتنوعة ومرطبات الجسم وحزم العطور المتنوعة.')
