INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008045, N'مونة / أسمنت / جص / إضافات جص', N'يشمل جميع المنتجات التي يمكن وصفها / ملاحظتها على أنها مادة مضافة للملاط ، ومركب التسوية ، والمعادل الشامل ، ولصق البلاط.', N'')
