INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008063, N'سكريد', N'يحتوي على جميع المنتجات التي يمكن وصفها بأنها خليط من الأسمنت البورتلاندي والرمل والماء. يتم استخدامه كأساس لأغطية الأرضيات الداخلية. باستثناء المنتجات مثل الخرسانة أو الرمل / الأسمنت.', N'')
