INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005673, N'أنظمة فتح باب الجراج', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مصمم للفتح والإغلاق ، إما بوابة أو باب مرآب عبر جهاز تحكم عن بعد. يتم شراء هذه الأنظمة كاملة ويمكن ربطها عالميًا بمعظم البوابات وأبواب المرآب. تتوفر هذه المنتجات في تصميمات وأنظمة ميكانيكية مختلفة تعتمد على متطلبات المستخدم. يتضمن منتجات مثل فتاحات أبواب المرآب المدفوعة بالسلسلة وفتحات البوابة المدفوعة بالحزام.', N'يُستثنى من ذلك منتجات مثل أجهزة الباب والأبواب الكاملة وكذلك فتاحات الأبواب اليدوية.')
