INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007000, N'كراج', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها هيكل خارجي مغلق بسقف ، مصمم خصيصًا لحماية المركبات من الطقس.', N'يستثنى من ذلك المنتجات مثل الأكواخ والشرفات الأرضية والمرآب.')
