INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007001, N'دبابيس وتد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قضيب صلب مصنوع عادة من الخشب أو البلاستيك أو المعدن ويتم إدخاله في ثقوب الاستقبال. دبابيس وتد تربط أجزاء التجميع بالاحتكاك. يمكن استخدامها أيضًا كدبابيس للمواقع. هذه المسامير شائعة في صناعة الأثاث.', N'باستثناء المنتجات مثل قضبان وتد.')
