INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006794, N'تصاعد القضبان', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كوحدة مستطيلة مفتوحة من البلاستيك المبثوق أو مادة معدنية. يمكن تطبيق هذه المنتجات على مجموعة متنوعة من الاستخدامات وبسبب طبيعتها العامة كما هي متوفرة في مجموعة متنوعة من الأحجام. يشمل منتجات مثل قضبان التثبيت للوحات.', N'باستثناء كتائف / تركيبات المصباح.')
