INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003182, N'المسامير / دبابيس (المثبتات / السحابات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أداة تثبيت مصنوعة من سلك لا نهاية له عن طريق قطع نقطة وتشكيل رأس عند طرف الساق المقابل للنقطة. وبالتالي فإن هذه المنتجات عبارة عن قطعة معدنية مدببة رفيعة يتم طرقها في المواد كنوع من التثبيت الدائم. تشمل منتجات مثل مسامير البناء ومسامير التسقيف ومسامير حلقية خارجية ومسامير مربعة الشكل.', N'باستثناء المنتجات مثل البراغي والغسالات والمسامير والمثبتات والمسامير وكذلك دبابيس وتد والبسكويت (DIY).')
