INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003184, N'المكسرات (المثبتات / السحابات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كتلة معدنية ذات سن لولبي داخلي يتم تركيبها على مسمار. عادة ما تكون الصواميل سداسية الأضلاع للسماح بشدها بسهولة باستخدام مفتاح ربط ، ولكنها قد تكون مجنحة أو مخرشة أو أي شكل آخر. جنبا إلى جنب مع صواميل البراغي مصممة لالتقاط الأشياء وربطها معًا. تشمل منتجات مثل المكسرات السداسية والمكسرات ذات الحواف والصواميل الجناح.', N'يستثنى من ذلك منتجات مثل البراغي والبراغي والمسامير والمثبتات والغسالات.')
