INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007930, N'باب ارضي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاجز مفصلي متحرك يوضع على الأرض ويغلق مدخل غرفة أو مبنى. يمكن تصميم هذه المنتجات للاستخدام الداخلي أو الخارجي.', N'')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003166, N'الحلقات / الحلقات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ثقب تحمي فتحة أو ثقب في مادة مثل القماش والجلد. يمكن استخدام هذه المنتجات لتقوية المادة التي توضع فيها أو لحماية مادة من الحواف الحادة للفتحة. تشمل منتجات مثل الحلقات المعدنية والحلقات المطاطية والحلقات المقاومة للعوامل الجوية.', N'يستثنى من ذلك المنتجات مثل فتحات الأحذية والخطافات وكذلك الخواتم التي يتم ارتداؤها على الأصابع.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003190, N'المسامير (المثبتات / السحابات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مسمار معدني أو دبوس له رأس في أحد طرفيه ويتم إدخاله من خلال ثقوب محاذاة في قطع المواد المراد ربطها ثم طرقها على الطرف العادي لتشكيل رأس ثانٍ. بشكل عام ، يتم تشكيل رأس برشام في المصنع ، وبناءً على نوع المادة ، قد يضطر المستخدم إلى تسخين البرشام لإنشاء رأس الربط الثاني. هذه المنتجات هي تثبيت دائم. تشمل منتجات مثل المسامير العمياء ومسامير اللسان الكسولة بالإضافة إلى المسامير ذات الرأس القبة.', N'باستثناء المنتجات مثل الدبابيس والمسامير والبراغي وكذلك البراغي والمثبتات والسلاسل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003167, N'الحبال / سلاسل / الكابلات (المثبتات / السحابات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها طول روابط معدنية صلبة أو طول ألياف ملتوية ومضفرة معًا للحصول على قوة إضافية. هذه المنتجات هي عناصر للأغراض العامة ويمكن استخدامها في مجموعة متنوعة من المواقف. تشمل منتجات مثل سلاسل السحب والكابلات والأسلاك غير الكهربائية.', N'باستثناء منتجات مثل الكابلات والأسلاك الكهربائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003181, N'مسامير', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قضيب أسطواني ملولب حلزونيًا يتعامل مع ثقب متشابه. تستخدم هذه المنتجات كمثبتات في النجارة وتحسين المنزل. يمكن تصميم هذه المنتجات خصيصًا للاستخدام مع مسدس لولبي للتغذية التلقائية. تتوفر البراغي بمجموعة متنوعة من الأحجام والمواد لتناسب الوظائف المطلوبة للمستهلكين. يشمل منتجات مثل براغي الشريط / التغذية التلقائية ، والبراغي ذات الرأس المسطحة ، والبراغي الفولاذية ذات الرأس المستديرة والمسامير ذات الفتحات القياسية.', N'يستثنى من ذلك منتجات مثل المسامير والمثبتات / سدادات الحائط والمسامير والسلاسل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003168, N'الينابيع', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملف معدني مرن ، والتي تعود إلى شكلها أو وضعها الأصلي بعد دفعها أو سحبها أو الضغط عليها. عادة ما تكون هذه المنتجات مصنوعة من الفولاذ المقوى. يشمل منتجات مثل النوابض الحلزونية.', N'باستثناء منتجات مثل الأسلاك والأنابيب وكذلك أجزاء التعليق للسيارات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003183, N'الدبابيس (المثبتات / السحابات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مسمار سلكي قصير على شكل حرف U لتأمين الكابلات ومواد التثبيت على القاعدة. يمكن عزل هذه المنتجات وإما أن يتم تشغيلها يدويًا أو تشغيلها آليًا. تتوفر هذه المنتجات بأحجام ومواد متنوعة حسب متطلبات المستخدم. تشمل منتجات مثل دبابيس الصلب المعزولة ودبابيس الصلب المدارة بالآلة.', N'يستثنى من ذلك منتجات مثل دبابيس القرطاسية والمسامير وكذلك المشابك والمشابك والمشابك.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003173, N'الأنابيب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كوحدة أسطوانية مجوفة من مادة بلاستيكية أو معدنية مبثوقة غير مدعمة. يمكن تطبيق هذه المنتجات على مجموعة متنوعة من الاستخدامات وبسبب طبيعتها العامة كما هي متوفرة في مجموعة متنوعة من الأحجام. تشمل منتجات مثل الأنابيب البلاستيكية وأنابيب الألومنيوم.', N'يستثنى من ذلك منتجات مثل مواد العزل ومنتجات المزاريب والصرف المستخدمة في السطح الخارجي للمنزل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003165, N'مشابك (مثبتات / مشابك)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها صامولة طويلة ، أسطوانية ، ملولبة داخليًا تستخدم لربط عناصر قضيب وقضيب قابل للتعديل. يتم تثبيت هذه الأجهزة بين الكابلات أو الحبال الممتدة وتستخدم لضبط الشد المطلوب. يشمل منتجات مثل مشابك الربط المطلية بالزنك.', N'باستثناء المنتجات مثل الصواميل والمسامير وكذلك المسامير والبراغي وكذلك جميع الأدوات اليدوية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003180, N'غسالات (المثبتات / السحابات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ختم يتكون من قرص مسطح موضوع لمنع التسرب وتوفير حماية إضافية للمادة المستهدفة التي تم تأمينها عليها. يمكن إغلاق الغسالات لزيادة القوة إلى التثبيت أو المسطح. تتوفر هذه المنتجات بأحجام وأشكال متنوعة تعتمد على الاستخدام المحدد. يشمل منتجات مثل غسالات الغسالات الفولاذية وغسالات الألمنيوم المسطحة.', N'باستثناء منتجات مثل مانعات التسرب والمواد اللاصقة وكذلك الصواميل والمسامير والبراغي والبطانات والمحامل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003926, N'حشوات زجاجية زخرفية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قسم زجاجي يوضع في باب أو نافذة لتحسين مظهره. يشمل الزجاج المعشق والزجاج الرصاصي والزجاج المزخرف.', N'يستثنى من ذلك منتجات مثل ألواح النوافذ والألواح البلاستيكية الحرارية الشفافة والكتل الزجاجية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002584, N'زجاج', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنه مادة صلبة غير متبلورة يتم الحصول عليها عند خلط السيليكا بمركبات أخرى ، وتسخينه فوق نقطة الانصهار ، ثم تبريده بسرعة. تشمل منتجات مثل ألواح النوافذ والزجاج المصقول وكذلك الزجاج المضاد للرصاص.', N'يستثنى من ذلك المنتجات مثل الألواح البلاستيكية الحرارية الشفافة والكتل الزجاجي والزجاج الداخلي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003925, N'بلوك زجاجي (أنظمة شبكية)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كتل زجاجية شفافة يتم تثبيتها مع ملاط أو شرائط بلاستيكية مع مادة مانعة للتسرب من السيليكون وتستخدم لبناء جدران أو حواجز غير حاملة.', N'باستثناء المنتجات مثل الألواح الزجاجية وألواح النوافذ وألواح البلاستيك الحراري الشفافة وإدراج الزجاج.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008143, N'بلاستيك حراري شفاف', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنه مادة بلاستيكية حرارية شفافة. تشمل منتجات مثل ألواح الأكريليك والبولي كربونات.', N'يستثنى من ذلك منتجات مثل الزجاج والكتل الزجاجية وإدخالات الزجاج المزخرفة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003956, N'قطع غيار / تركيبات صنبور', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كنظام من الأنابيب والأجزاء التي تسمح بالتدفق المتحكم فيه لمياه الصرف عموديًا نحو نظام الصرف أو نظام التخلص من المياه الجوفية. تشمل منتجات مثل الخشب والألواح المعدنية المجلفنة والنحاس والألمنيوم ومنتجات الفينيل.', N'يستثنى من ذلك منتجات مثل أجزاء / تركيبات صرف العشب وأجزاء / تركيبات المزاريب وكذلك المصارف ومنافذ التسرب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005668, N'أغطية الصرف (المزاريب / الصرف)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كغطاء أو شبكة مصممة خصيصًا لتوضع فوق أنبوب أو أنبوب يسمح بالتصريف الكامل لأي مياه زائدة في المنطقة. عادة ما تكون هذه المنتجات أغطية مؤقتة ويمكن إزالتها للسماح بفك الأنبوب. يشمل منتجات مثل شبكات الصرف المعدنية وأغطية الصرف الخشبية المركبة.', N'باستثناء منتجات مثل تركيبات أنابيب الصرف وأجزاء / تركيبات تصريف العشب وكذلك أجزاء / تركيبات السباكة ، والتي ترتبط مباشرة بتركيب المنتجات المنزلية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007013, N'شبكات الصرف (المزاريب / الصرف)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها غطاء تصريف مفتوح ، مصممة خصيصًا لمنع المواد مثل الأوراق من الوصول إلى البالوعة ، مع استمرار السماح بمرور السوائل.', N'لا يشمل شبكات الصرف المستخدمة في المنزل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003955, N'منافذ الإسقاط (المزاريب / الصرف)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كوحدة هيكلية تُستخدم لتحويل المياه بعيدًا عن المبنى لمنع تلف الأساسات والجدران الخارجية. تتأرجح هذه المنتجات للسماح بالجز حول محيطها المباشر. تشمل منتجات مثل الخشب والألواح المعدنية المجلفنة والنحاس والألمنيوم ومنتجات الفينيل.', N'يستثني منتجات مثل أجزاء المزراب / التركيبات وأجزاء / تركيبات صنبور السفلية وأجزاء / تركيبات تصريف العشب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002665, N'أجزاء المزاريب / التركيبات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها طول من الأنابيب يتم تثبيتها أفقيًا على جدار أو أفاريز المبنى لنقل مياه الأمطار بعيدًا عن السطح الخارجي حيث يمكن أن تتسبب في حدوث ضرر. تساعد أجزاء المزاريب في نقل مياه الأمطار نحو مواسير الصرف أو المصارف حيث يتم التخلص من المياه بأمان. تتوفر هذه المنتجات بأحجام ومواد مختلفة ويمكن تكييفها مع العديد من الأجزاء المختلفة للاستخدام في معظم المباني. يشمل منتجات مثل أنابيب / أنابيب مزاريب بلاستيكية منزلية وانحناءات ووصلات مزاريب سيراميك.', N'يستثنى من ذلك جميع مواسير ووصلات ماسورة التصريف وكذلك أجزاء وتركيبات الصرف في الحديقة ومنافذ التسرب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002666, N'المزاريب / الصرف أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجات مزراب / صرف ، حيث يتعذر على مستخدم المخطط تصنيف المنتجات الموجودة في الطوب الموجود داخل المخطط.', N'يستثنى من ذلك جميع منتجات المزاريب / الصرف المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002664, N'حزم متنوعة المزاريب / الصرف الصحي', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما اثنان أو أكثر من منتجات مزاريب أو تصريف مميزة تباع معًا ، والتي توجد ضمن المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمن في نفس العبوة التي تتقاطع مع الطوب ضمن فئة المزاريب / الصرف الصحي. تشمل منتجات مثل أجزاء صنبور السفلية وأجزاء المزاريب التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك المنتجات مثل العبوات المتنوعة المزاريب وأوعية المياه.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002663, N'أجزاء / تركيبات صرف العشب / مستوى الأرض', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأنابيب مصممة خصيصًا لإزالة المياه الزائدة من الأرض أو التربة في منطقة محددة. تجمع هذه المنتجات المياه وتوجهها عبر الأنابيب إلى حامل المياه المحلي أو إلى مصدر المياه الجوفية. تشمل منتجات مثل أنابيب الخشب والألواح المعدنية المجلفنة والنحاس والألومنيوم والفينيل وكذلك أنابيب الصرف البلاستيكية أو المركبة تحت الأرض.', N'يستثني منتجات مثل شبكات الصرف (المزاريب / الصرف) ، أجزاء / مواسير التصريف ، أجزاء المزاريب / التركيبات ومنافذ التسرب.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002454, N'العزل - الواح / لفات / بطانيات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها إما بطانية سميكة تتكون من ألياف مرنة (غالبًا الصوف المعدني والألياف البلاستيكية والألياف الطبيعية ، مثل القطن وصوف الأغنام) أو لوح صلب أو ناعم يتكون من رغوة (مبثوقة ، ممدد أو رغوي رطب أو ليس بين طبقات ثابتة). تم تصميم هذه المنتجات لأغراض العزل الحراري و / أو الصوتي.', N'يستثنى من ذلك منتجات مثل رغوة الرش.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002456, N'العزل - تعبئة سائبة / رغوة رش', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كشكل من أشكال العزل الذي يتم سكبه أو نفخه في التجاويف لتقليل فقد الحرارة أو لتعزيز خصائص العزل الصوتي. يمكن لهذه المنتجات عند تطبيقها بشكل صحيح أن تقضي فعليًا على ترشيح الهواء المهدر للطاقة في الغرف العلوية.', N'يستثنى من ذلك منتجات مثل العزل - الألواح / البكرات / البطانيات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005733, N'العزل - تباطؤ / تغليف الأنابيب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة متأخرة لتغليف أنبوب واحد أو عدة أنابيب لتقليل فقد الحرارة والتكثف أو لتعزيز خصائص العزل الصوتي.', N'يستثنى من ذلك منتجات مثل العزل - الألواح / اللفات / البطانيات والعزل - الحواجز المشعة / الدروع الحرارية.')
