INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002460, N'عزل أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجات عزل ، حيث لا يتمكن مستخدم المخطط من تصنيف المنتجات الموجودة في الطوب الموجود داخل المخطط.', N'يستثنى من ذلك جميع منتجات العزل المصنفة حاليًا.')
