INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007929, N'بخاخات مانعة لتسرب المياه / عوامل تشريب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كطبقة إضافية مصممة ليتم تطبيقها على المواد الموجودة لأغراض العزل المائي. تم تصميم هذه المنتجات لزيادة كفاءة المواد الموجودة.', N'يستثنى من ذلك المنتجات مثل مواد العزل المائي للأحذية أو المنسوجات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002537, N'خشب هندسي (الأبعاد / إنشائي)', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنه منتجات خشبية معاد تدويرها / معاد تشكيلها مغلفة أو "مفصولة بالأصابع". قد يحتوي الخشب على ملوثات مثل المواد اللاصقة المعالجة والرقائق والمثبتات. تشمل منتجات مثل ألواح الخشب المضغوط ، والألواح الليفية ، والعوارض الخشبية المصفحة ، والرافعات على شكل حرف I.', N'يستثنى من ذلك المنتجات مثل الخشب الصلب ، والخشب اللين ، والخشب الرقائقي ، وألواح الجدائل الموجهة (OSB) وكذلك ألواح الجبس.')
