INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002540, N'ألواح الجبس / الأسمنت', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها لوحة مواجهة داخلية أو خارجية تتكون من قلب جبس محصور بين أوجه الورق أو منتج من ألياف الخشب الأسمنتي يستخدم كلوح دعم. تُعرف أيضًا باسم الحوائط الجافة أو اللوح الجصي ، ويمكن أيضًا استخدام هذه المنتجات في بناء الأسقف. يشمل لوح دعم البلاط الأسمنتي ، أو ألواح الجبس الداخلية.', N'يستثنى من ذلك المنتجات مثل ألواح الخشب اللين وكذلك الألواح الخشبية المصممة هندسيًا.')
