INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003932, N'خشب / لوح خشب / جبس اخر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجات Lumber أو Wood Panel أو Gypsum ، حيث لا يكون مستخدم المخطط قادرًا على تصنيف المنتجات في الطوب الموجود داخل المخطط.', N'يستثنى من ذلك جميع منتجات الأخشاب / الألواح الخشبية / الجبس المصنفة حاليًا.')
