INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005424, N'أقطاب / بلينجز / قضبان', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها عمود خشبي أو كومة أو قضيب طويل ومستدير عادةً ويستخدم بشكل أساسي في البناء ، لا سيما كدعم للأعضاء الهيكلية الأخرى في المبنى ، ولكن أيضًا كدعامات عامة متعددة الأغراض في كل من المنزل والحديقة. يشمل منتجات مثل الدعائم ، التي يتم دفعها إلى الأرض لدعم أسس الهيكل وكذلك قضبان وتد متعددة الأغراض.', N'يستثنى من ذلك منتجات مثل أعمدة السياج والخشب اللين.')
