INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003939, N'المطاحن - الحلي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملحقات تستخدم لتكملة القوالب ، وتقليم الباب وتزيين النوافذ. يمكن أن تكون هذه المنتجات إما مصنوعة من الخشب أو من مادة مركبة ويمكن استخدام بعضها خارجيًا في المنزل. تشمل منتجات مثل قوالب القاعدة والوريدات.', N'باستثناء المنتجات مثل القوالب وأجزاء السلم.')
