INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003934, N'القوالب - غير الخشبية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها زخارف زينة مضافة إلى الأسطح المسطحة والأعمدة والأفاريز. يتم تثبيت هذه المنتجات إما داخليًا أو خارجيًا اعتمادًا على خصائصها المقاومة للعوامل الجوية. يشمل منتجات مثل قالب لوحة مُجهز أو تاج مبثوق بالفينيل.', N'يستثنى من ذلك منتجات مثل القوالب الخشبية وأعمال المطاحن.')
