INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002512, N'مشاركات Newel', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنه أعمدة أو أعمدة تدعم الدرابزين وتكون مستديرة أو منحنية في الجزء العلوي والسفلي من الدرج. هذه الدعامات أكبر من أي عمود رأسي في الدرابزين وقد تكون مزخرفة جدًا. يشمل منتجات مثل المنشورات الجديدة العادية أو المزخرفة.', N'يستثنى من ذلك منتجات مثل الدرابزين وكذلك الأعمدة والأعمدة المستخدمة لغرض مختلف بخلاف دعم الدرابزين.')
