INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006998, N'مرآب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مبنى خارجي مفتوح أو شبه مفتوح من طابق واحد يستخدم لوقوف السيارات وتوفير الحماية من سوء الأحوال الجوية. يمكن أن يكون الهيكل قائمًا بذاته أو متصلًا بجدار. على عكس معظم الهياكل ، لا يحتوي المرآب على أربعة جدران ، وعادة ما يكون به واحد أو اثنان.', N'يستثنى من ذلك المنتجات مثل مواقف السيارات المغلقة أو المرائب.')
