INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006791, N'التعامل مع الأحجار', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أعلى حجر في جدار أو هيكل يكون غطاءً لجدار ، يختلف عن الأحجار المستخدمة في جدار الهيكل. تعتبر اللمسة الأخيرة.', N'')
