INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008046, N'زاوية الشحذ / التجصيص موصل الملف الشخصي', N'يشمل جميع المنتجات التي يمكن وصفها / ملاحظتها كدليل يمكن استخدامها للتجصيص.', N'')
