INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002686, N'ألواح / ألواح التسقيف', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستطيلات كبيرة من مواد التسقيف التي يتم تركيبها معًا لتشكيل سقف. هذه الألواح أكبر من الألواح أو البلاط وتشكل عند تركيبها تعزيزًا صارمًا للهيكل الموجود تحتها. قد تكون هذه المنتجات معدنية أو بلاستيكية أو لائحة مقلدة. تشمل منتجات مثل ألواح التسقيف المعدنية الصلبة.', N'يُستثنى من ذلك منتجات مثل بلاط الأسقف وبلاط الأرضيات وقشرة الحجر بالإضافة إلى أرضيات الحجر المستخدمة في الحديقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002683, N'بلاط التسقيف / الأردواز / القوباء المنطقية / يهز', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مترابطة / متداخلة ، تكون في الغالب رقيقة وأحيانًا على شكل إسفين ، مستطيلات من المواد المستخدمة لتغطية السقف ، بما في ذلك المنتجات المصنوعة من الطين أو الأسمنت أو المعدن أو البلاستيك أو الألياف الزجاجية أو الحجر الطبيعي أو الخشب أو قطع مستطيلة مسطحة من الأردواز أو الألياف المعدنية أو الألياف الزجاجية أو الأسفلت المركب على سطح لمنع تسرب المياه بشكل أساسي.', N'يستثنى من ذلك المنتجات مثل بلاط الجدران الخزفي والأرضيات الحجرية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002693, N'حزم متنوعة تسقيف', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجين أو أكثر من منتجات الأسقف / التشطيبات الخارجية التي تباع معًا ، والتي توجد داخل المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تتقاطع الطوب ضمن فئة التسقيف / الخارجي. تشمل منتجات مثل أغشية التسقيف المباعة بألواح التسقيف. يجب إزالة العناصر المستلمة مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل مواد التسقيف المباعة مع Loft Insulation.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003208, N'السد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أسمنت بلاستيكي مصطكي أو إسفلتي يستخدم لملء الفجوات والفواصل لمنع التسربات. السد مادة مرنة يمكن استخدامها لسد فجوة بين سطحين مثل الألواح الجانبية أو الزوايا في جدران حوض الاستحمام. يشمل منتجات مثل السد الخارجي والسد الداخلي.', N'يستثنى من ذلك منتجات مثل الحشو والجص والعزل وكذلك الأسمنت.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008000, N'مجمع انشاءات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مركب ترابط لربط الألواح الجصية مباشرة بالجدران المسطحة والآمنة ، مثل البناء والطوب ، عن طريق تطبيق المركب في نقاط وألواح متباعدة بشكل متساوٍ عبر سطح الجدار أو اللوح الجصي.', N'يستثنى من ذلك منتجات مثل الجص خفيف الوزن سريع التركيب ، والجبس الأسمنتي ، والجبس الجيري ، والجبس الترابي ، والأسمنت ، والجص ، وخلائط الملاط ، وجص باريس (جص جبسي).')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007722, N'وكلاء فحص العيوب / الكراك', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كعامل للكشف عن عيوب المواد. مصممة خصيصًا للكشف عن شقوق المعالجة وشقوق التعب والمسامية وشقوق الطحن وأعطال اللحام وعيوب السطح الأخرى.', N'يستبعد أي نوع من المواد المانعة للتسرب أو الحشو أو المواد اللاصقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003205, N'الحشو', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حشو للفجوات والثقوب ، مما يؤدي إلى تكوين سطح مستوٍ وغير متقطع. يشمل منتجات مثل المعجون الجاهز ، أو البودرة التي يتم خلطها قبل وضعها على السطح المستهدف ، أو الأشرطة الخشبية المستخدمة لملء الفجوات عند صنع الخزانة.', N'يستثنى من ذلك منتجات مثل الجص والجص ومانع التسرب والجلفطة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003207, N'غراء / لاصق', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة لاصقة للتلامس. تتوفر هذه المنتجات في العديد من الأشكال المختلفة ويمكن إما أن تكون مصممة خصيصًا لسطح معين أو يمكن أن تكون متعددة الأغراض. يشمل منتجات مثل الغراء الصلب والغراء السائل ، اللاصق اللاصق والغراء الجاهز للاستخدام.', N'يستثنى من ذلك منتجات مثل غراء القرطاسية وغراء الفنون والحرف اليدوية وكذلك السد ، مانع التسرب والجص.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003203, N'جصص', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملاط رقيق يستخدم في أعمال البناء لملء الفواصل بين الطوب والكتل والبلاط. هذه المنتجات عبارة عن خليط من الأسمنت والرمل والماء وتستخدم أحيانًا أسفل ألواح القاعدة للحصول على أسطح تحمل موحدة. يشمل منتجات مثل Tiling Grout و Resin Grout.', N'يستثنى من ذلك منتجات مثل الحشو ، مانع التسرب والجلفطة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003204, N'مانعات التسرب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمركب يستخدم لملء وختم الوصلة. تحتوي هذه المنتجات على مادة أساسية تتغير بناءً على السطح المستهدف للتطبيق. يتم تحديد مواد منع التسرب إما للاستخدام الداخلي أو الخارجي ولكن يمكن ، في بعض الحالات ، تطبيقها في كلتا البيئتين. يشمل منتجات مثل مانع التسرب الخارجي المقاوم للعفن ومانع التسرب ذو الأساس المائي وأغشية الختم وشرائط الختم.', N'يستثنى من ذلك منتجات مثل الجص والحشو والغراء والسد وكذلك الجص والأسمنت.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003210, N'مانعات التسرب / الحشو / المواد اللاصقة / عوامل عيوب أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة مانعة للتسرب أو حشو أو مادة لاصقة ، حيث يتعذر على مستخدم المخطط تصنيف المنتجات الموجودة في الطوب الموجود داخل المخطط.', N'يستثنى من ذلك جميع المواد المانعة للتسرب أو الحشو أو المنتجات اللاصقة المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003211, N'عبوات متنوعة مانعات التسرب / الحشو / المواد اللاصقة / عوامل العيوب', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما منتجان أو أكثر من منتجات مانع التسرب أو مواد الحشو أو المواد اللاصقة المميزة التي تباع معًا ، والتي توجد داخل المخطط والتي تنتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة عبر الطوب داخل حشو مانع التسرب / فئة المواد اللاصقة. يشمل منتجات مثل الغراء المباعة بشريط لاصق. يجب إزالة العناصر المستلمة مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يُستثنى من ذلك منتجات مثل Grout التي تُباع مع Tiles.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003206, N'الشريط (DIY)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قطعة طويلة من القماش أو الورق الرفيعة المستخدمة للتجليد أو التثبيت ، وغالبًا ما تكون مغلفة بمادة لاصقة على أحد الجانبين أو كلاهما. يشمل منتجات مثل شريط لاصق وشريط لاصق.', N'يستثنى من ذلك منتجات مثل الحشو والسد والعزل وكذلك أشرطة القرطاسية وأشرطة الصوت.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003966, N'الديكور الخارجي - Corbels / الأقواس', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كتلة حجرية بارزة مدمجة في جدار أو سقف أثناء البناء ؛ بناء متدرج ، كما هو الحال في القوس. يتم استخدام Corbels إما لدعم الإسقاطات الأخرى فوقها (مثل الأفاريز وأغطية النوافذ) أو لأسباب زخرفية بحتة. تكون الأقواس ذات قمة أو مدورة أو لوحات زخرفية أخرى فوق المداخل ، وعادة ما تكون مثلثة الشكل ولكن يمكن تمريرها أو قطعها. تشمل منتجات مثل الجملونات أو الزخارف المزخرفة أو الأقواس الممزقة.', N'يستثنى من ذلك منتجات مثل القوالب / عناصر المطاحن ، الزخرفة الخارجية - الجوانب ، الزخرفة الخارجية - القشرة والبلاط / الألواح المعدنية وألواح التسقيف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002677, N'تقليم الخارجي - انحياز', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كغطاء مطبق على السطح الخارجي للمبنى لجعله مقاومًا للعوامل الجوية بالإضافة إلى تحسين جماليات السطح الخارجي للمبنى. يشمل المنتجات المصنوعة من الخشب المصمم هندسيًا والخشب الطبيعي والألمنيوم والفينيل ، والتي يتم تجميعها معًا باستخدام أدوات النجارة والمقرنات وحواف الزوايا.', N'يستثنى من ذلك منتجات مثل ألواح التسقيف والبلاط والألواح الخشبية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005705, N'تقليم خارجي - ملحقات انحياز', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملحقات تستخدم في تجميع جوانب الجدار الخارجي. يشمل منتجات مثل النجارين والمثبتات وحواف الزوايا.', N'يستثنى من ذلك منتجات مثل القشرة ، والحواف والأقواس.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005731, N'الزخرفة الخارجية - الحواف / الدعامة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ألواح من الخشب أو الحجر أو الفينيل ملتصقة بقاعدة مبنى أو هيكل مرتفع مثل منزل متنقل أو سقيفة. تضيف هذه المنتجات لمسة نهائية أنيقة وتبقي الحيوانات خارج المنزل وتساعد في عزل المنازل المتنقلة.', N'يستثنى من ذلك منتجات مثل الجوانب الخارجية ، والحواف الخارجية ، وألواح التسقيف والبلاط.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003969, N'تقليم خارجي - قشرة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها طبقة رفيعة من الخشب أو الحجر ملتصقة بوجه الركيزة. يتم تقطيع هذه الألواح الرقيقة جدًا من الخشب أو الحجر أو الطوب من المواد الأصلية واستخدامها لتغطية الجزء الخارجي من جدار المنزل ، لخلق وهم بهيكل أكثر "صلابة". يشمل منتجات مثل قشرة الطوب والحجر بالإضافة إلى قشرة الخشب.', N'يستثنى من ذلك منتجات مثل الزخرفة الخارجية - انحياز ، لوح خشبي ، أرضيات حجرية ، حواف خارجية - كبلات / أقواس وأسقف - القوباء المنطقية / الاهتزازات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002530, N'أعمدة (إنشائية)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أعمدة رفيعة مصممة لتحمل حمولة متراكبة. تستخدم الأعمدة بشكل متكرر لدعم الحزم أو الأقواس التي ترتكز عليها الأجزاء العلوية من الجدران أو الأسقف. يشمل منتجات مثل الأعمدة المقواة والأعمدة الزخرفية.', N'يُستثنى من ذلك المنتجات مثل أعمدة النيو وأعمدة السياج وكذلك دعامات I-Beams ودعامات الأسقف والأرضيات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002533, N'دعامات أرضية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأعضاء هيكلية هندسية تستخدم في بناء الأرضيات. هذه المنتجات تدعم وزن كل شيء فوقها. يشمل منتجات مثل دعامات الويب المفتوحة.', N'باستثناء المنتجات مثل دعامات السقف ،')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008281, N'المكونات المعدنية (الأبعاد / الهيكلية)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مكون معدني رفيع (مقاطع جانبية أو شماعات) معدة للاستخدام كمواد بناء. يمكن استخدام هذه المنتجات كإطار لأنظمة ألواح الجبس غير الحاملة مثل الجدران الفاصلة وبطانات الجدران والأسقف.', N'باستثناء منتجات مثل الأعمدة (الهيكلية) ، و I-Beams ، وألواح البناء XPS وعناصر البلاط ، وخشب الخشب اللين (الأبعاد / الهيكلية) ، والأخشاب الصلبة (الأبعاد / الهيكلية) ، والخشب الهندسي (الأبعاد / الهيكلية).')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005732, N'تجميع المنزل المحمول / معدات الإعداد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها معدات محددة مستخدمة في تجميع وتحديد موقع منزل متنقل. يشمل منتجات مثل مثبتات المنزل المحمول ، والمعادلات ، والربط والأوتاد.', N'يستثنى من ذلك منتجات مثل مثبتات الأجهزة العامة والمثبتات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005659, N'دعامات السقف', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها شبكة هيكلية من الأخشاب التي تشكل هيكلًا صلبًا لدعم سقف المبنى. تدعم مكونات المبنى المصممة هندسيًا السقف بدلاً من العوارض الخشبية. عادة ما يتم بناء دعامات السقف على شكل مثلث مع عدد من القطع المترابطة التي تنشر الحمل بالتساوي عبر الجمالون. يشمل منتجات مثل دعامات السقف البلوطية المثلثة ودعامات السقف المرتفعة.', N'يستثنى من ذلك منتجات مثل دعامات الأرضيات وألواح الأسقف والأخشاب ومنتجات الأخشاب بالإضافة إلى الأعمدة وعوارض I.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002534, N'الحزم الهيكلية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها شعاع إنشائي وهو مكون مستخدم في البناء لإضافة قوة إلى أي هيكل أو تصميم. يتم تصنيع العارضة الإنشائية من الفولاذ أو الخرسانة أو الخشب ، وعادة ما تستخدم لتمديد عنصر مفتوح من الهيكل ، وكذلك لتوفير الدعم أسفل مكون ثقيل للغاية من الهيكل. تشمل منتجات مثل عوارض حديد I وعتبات خرسانية وما إلى ذلك.', N'يستثنى من ذلك منتجات مثل دعامات الأرضيات والأسقف والأعمدة والأعمدة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003942, N'المكونات الهيكلية / التجميعات أخرى', N'يشمل أي منتجات قد يتم وصفها / ملاحظتها على أنها منتجات الأجهزة الهيكلية ، حيث لا يتمكن مستخدم المخطط من تصنيف المنتجات في الطوب الموجود داخل المخطط.', N'يستثني جميع منتجات الأجهزة الإنشائية المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003943, N'المكونات الهيكلية / قطع غيار التجميعات / الملحقات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كملحق أو جزء بديل للأجهزة الإنشائية. تشمل منتجات مثل روابط الإعصار والحشوات والفواصل.', N'باستثناء جميع المنتجات الأخرى المصنفة حاليًا ضمن فئة الأجهزة الإنشائية ، مثل I-Beams ودعامات الأرضيات ودعامات الأسقف. يستثني أيضًا منتجات مثل المثبتات بما في ذلك المشابك والمسامير والخطافات.')
