INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007608, N'الديكورات الحافة', N'يشمل أي منتجات يمكن استخدامها لتنعيم حافة الزاوية المكونة من سطحين أو أكثر لأغراض التصميم أو الأداء. يمكن أن يكون العنصر مصنوعًا من الخشب والبلاستيك ومواد أخرى.', N'باستثناء الألواح الأساسية والطبقات السفلية.')
