INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002446, N'الأرضيات - السجاد', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كأغطية أرضية مكونة من ألياف نسيجية مثبتة حول المنزل. يمكن أن تكون هذه المنتجات داخلية أو خارجية للمنزل ومتوفرة في العديد من الأساليب المختلفة. تشمل منتجات مثل سجاد الصوف.', N'يستثنى على وجه التحديد السجاد والحصير وكذلك العشب الصناعي. يستثنى من ذلك منتجات مثل تعليقات وأغطية الجدران النسيجية.')
