INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002548, N'أقفال / براغي للنافذة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أداة تثبيت مثبتة في النافذة لإبقائها مغلقة بإحكام. يشمل منتجات مثل قفل النافذة الهلالي ، قفل النافذة الوشاح ، الترباس المنزلق.', N'باستثناء منتجات مثل أقفال نوافذ السيارة وأقفال الأبواب.')
