INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008123, N'دلاء', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية مانعة لتسرب الماء والتي قد يكون لها مقبض مرفق. بدون عجلات أو مرفقات مصفاة.', N'يستبعد: دلاء الثلج / النبيذ ؛ أحواض الهاون / دلاء البناء')
