INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008124, N'مضارب السجاد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أداة تنظيف منزلية تستخدم للتغلب على السجاد لنفض الغبار والأوساخ عنه.', N'')
