INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008136, N'مساعدات الغسيل المعلقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة مساعدة أو أداة لتعليق الغسيل المبلل مثل مشابك الملابس.', N'')
