INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000556, N'طقم / إصلاحات مزيل الخدوش', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها فرشاة أو سائل مصمم خصيصًا لتطبيقه على خدش على سطح خشبي / معدني لإزالة أو تغطية الرؤية.', N'باستثناء منتجات مثل الزيوت / المزلقات وملء الخدوش / مستحضرات إصلاح البلاستيك (السيارات).')
