INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008128, N'ممسحة دلو الجمعية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مجموعة دلو وجرس تسمح لمستخدمها بعصر ممسحة مبللة دون اتساخ اليدين. قد تكون مزودة بعجلات ولها آلية احتواء أو آلية منفصلة.', N'باستثناء عربات الحراسة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008126, N'ممسحة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تنظيف تتكون من رغوة أو قطعة قماش أو شرائط قماشية أو وسادات أو خيوط من خيوط مع مقبض أو عصا مكنسة ، تُستخدم عمومًا مع الماء أو محلول التنظيف.', N'باستثناء مجموعة دلو الممسحة.')
