INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10004113, N'ملابس رياضية - برقبة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كعنصر أو لباس مصمم خصيصًا للارتداء حول الرقبة أو حولها أثناء ممارسة الرياضة. تشمل منتجات مثل الباندانا والأوشحة والرباطات وربطات العنق وربطات العنق.', N'يُستثنى من ذلك منتجات مثل قلادات المجوهرات والرقبة الرياضية المحددة.')
