INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006840, N'ملابس رياضية - أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملابس رياضية ، حيث يتعذر على مستخدم المخطط تصنيف المنتجات الموجودة في قوالب موجودة داخل المخطط.', N'باستثناء جميع الملابس الرياضية وملابس السباحة / المايوه المصنفة حاليًا.')
