INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001344, N'ملابس رياضية - ملابس الجزء العلوي من الجسم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملابس يتم ارتداؤها على الجزء العلوي من الجسم أثناء ممارسة الرياضة. تشمل منتجات مثل قمصان كرة القدم.', N'باستثناء المنتجات مثل ملابس السباحة / ملابس السباحة وملابس الجزء العلوي من الجسم غير المصممة خصيصًا لممارسة الرياضة مثل ملابس الجزء العلوي من الجسم اليومية والأغراض العامة وملابس الجزء العلوي من الجسم الواقية.')
