INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001326, N'أحزمة / حمالات / Cummerbunds', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها إما رباط يوضع حول الخصر لربط الملابس ، أو في حالة المشابك ، شريطان يمتدان من مقدمة البنطلون ، فوق كل كتف إلى الخلف ويساعدان على الصمود السراويل. غالبًا ما تكون هذه المنتجات مزخرفة وتساعد في إضفاء الإكسسوارات على الملابس.', N'باستثناء منتجات مثل الأوشحة وربطات العنق وأحزمة الحماية وأحزمة رياضية معينة.')
