INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006903, N'شريط الملابس / الموضة', N'يشمل أي منتجات على الوجهين يمكن وصفها / ملاحظتها كعنصر مصمم لتأمين الملابس في مكانها بينما تظل مخفية. يمكن استخدامه لإصلاح تنحنح ، أو تثبيت الفستان في مكانه أو حتى تثبيت حزام حمالة الصدر في مكانه.', N'باستثناء المنتجات مثل الشريط على الوجهين المستخدم في الأدوات المكتبية والفنون والحرف اليدوية.')
