INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001328, N'ملابس يدوية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملابس مصممة خصيصًا للارتداء على اليدين كملابس أو إكسسوار أزياء. تشمل منتجات مثل القفازات والقفازات والقفازات بدون أصابع.', N'يستثنى من ذلك منتجات مثل الملابس اليدوية الواقية وملابس رياضية محددة ومجوهرات لليدين.')
