INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001334, N'تنانير', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملابس تتدلى من الخصر ، مع فتحة واحدة للساقين. يشمل جميع المنتجات (على سبيل المثال العسكرية ، خدمة الطوارئ ، الصناعية أو المواصفات الخاصة الأخرى) التي تناسب تعريف المنتج.', N'يُستثنى على وجه التحديد التنورات التي يتم ارتداؤها لممارسة الرياضات مثل الهوكي وكرة الشبكة. باستثناء منتجات مثل الفساتين والتنانير الرياضية.')
