INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001350, N'جاكيتات / بليزرات / كارديجان / صدريات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملابس تغطي الجزء العلوي من الجسم ، بأكمام أو بدون أكمام ، والتي يتم تثبيتها من الأمام وعادة ما يتم ارتداؤها فوق الملابس الأخرى. يشمل جميع المنتجات (على سبيل المثال العسكرية ، خدمة الطوارئ ، الصناعية أو المواصفات الخاصة الأخرى) التي تناسب تعريف المنتج.', N'باستثناء المنتجات مثل المعاطف التي يتم ارتداؤها كملابس خارجية لتوفير الحماية من المطر والرياح والبرد ، وكذلك السترات التي يتم ارتداؤها أثناء النشاط الرياضي.')
