INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003586, N'نظارات / نظارات واقية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها نظارات يتم ارتداؤها لحماية العينين وفي بعض الحالات الوجه بالكامل. يمكن تركيب هذه المنتجات بالقرب من الرأس وحول العينين. تشمل منتجات مثل النظارات الواقية ، ونظارات الأمان (بما في ذلك نظارات السلامة التي تُصرف بوصفة طبية) وواقيات الوجه الكاملة.', N'باستثناء المنتجات مثل واقيات اللحام والنظارات الشمسية والنظارات الطبية.')
